import React, { useState } from "react";
import "./TemplateStyles/ReportTemplate.css";

export function ReportTemplate() {
    const default_report =
        "Can you write me a { type } report using the following rules:" +
        "- duration of { duration } months," +
        "- the main subject is: { subject }," +
        "- my name is : { lastname } { firstname }";

    const [report, setReport] = useState(default_report);

    return (
        <div className="Report-template">
      <textarea
          className="Text-box"
          value={report}
          onChange={(event) => setReport(event.target.value)}
      ></textarea>
        </div>
    );
}
