import React, { useState, useEffect, useRef } from "react";
import "./TemplateStyles/ReportTemplate.css";

export function ReportTemplate() {
    const default_report =
        "Can you write me a { type } report using the following rules:\n" +
        "   - duration of { duration } months.\n" +
        "   - the main subject is: { subject }.\n" +
        "   - my name is : { lastname } { firstname }.";

    const [report, setReport] = useState(default_report);
    const textareaRef = useRef(null);

    useEffect(() => {
        if (textareaRef.current) {
            textareaRef.current.style.height = "auto";
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
        }
    }, [report]);

    return (
        <div className="Report-template">
      <textarea
          className="Text-box"
          ref={textareaRef}
          value={report}
          onChange={(event) => setReport(event.target.value)}
          style={{ resize: "none" , fontSize: "15px"}} // Set the resize property to none
      ></textarea>
        </div>
    );
}
